mapper.select:
select board_seq ,(select category_name from category C where C.category_code = B.category_code) as category_name ,title ,content ,image1 ,image2 ,image3 ,image4 ,image5 ,image6 ,price ,(select user_id from member M where M.userid = B.user_id) as user_id ,(select sum(zzim) from zzim Z where Z.board_seq=B.board_seq) as zzim_cnt ,hit ,(select images from member M where M.user_seq = B.user_seq) as user_images ,wdate ,rnum from ( select b.board_seq ,b.category_code ,b.title ,b.content ,b.image1 ,b.image2 ,b.image3 ,b.image4 ,b.image5 ,b.image6 ,b.price ,b.user_id ,b.zzim_cnt ,b.hit ,b.user_seq ,b.wdate ,@rownum := @rownum+1 as rnum from board B, (select@rownum:=0) R
where category_code like #{keyword}
where user_id=#{user_id}
order by board_seq desc ) B limit #{start}, #{pageSize}
