mapper.select:
select board_seq , category_code , title , (select user_id from user where b.user_seq = user.user_seq) user_id , user_seq from board b
where title like concat('%', #{keyword}, '%') or content like concat('%',#{keyword}, '%') or user_seq = (select user_seq from user where user.user_id like concat('%', #{keyword}, '%'))
order by board_seq desc limit #{start}, #{pageSize}
